package Lec_01;

public class Arthimatic_operation {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=5;
		int b=7;
		int c=a+b;
		System.out.println(c);
		c=a-b;
		System.out.println(c);
		int d=c-a;
		System.out.println(d);
		int x=9;
		int y=11;
		int z=x*y;
		System.out.println(z);
		int p=59;
		int q=9;
		int r=p/q;
		System.out.println(r);
		int k=56;
		int l=8;
		int n=k/l;
		System.out.println(n);
		
	}

}
