package Lec_01;

public class max_of_three_number {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		int b=110;
		int c=90;
		if(a>b && a>c) {
			System.out.println(a);
		}
		else if (b>a && b>c) {
			System.out.println(b);
		}
		else {
			System.out.println(c);
		}
	}

}
